sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	// || newValue == oldValue added to avoid running the script if the value hasn't changed
   if (newValue === '' || newValue == oldValue) {
      return;
   }
	//Initiate GetEmailAddress Script Include
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	//specify getEmail method
	getEmailAddr.addParam('sysparm_name','getEmail');
	//Pass the Id of the user in the Requested for field required by GetEmailAddress
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	//Send the request to the server and pass teh returned XML to the populateEmailField function
	getEmailAddr.getXML(populateEmailField);

	//called when the server responds by the line: getEmailAddr.getXML(populateEmailField)
	function populateEmailField(response){
		//console.error(response.responseXML);
		//Extract the Email from the response
		var returnedEmailAddr = response.responseXML.documentElement.getAttribute("answer");
		//clear previous input from the "Request for email" field
		g_form.clearValue('u_requested_for_email');
		//set the value of the "requested for email" field to the email extracted from the response
		g_form.setValue('u_requested_for_email',returnedEmailAddr);
	}
}
