sys_script_include.script:
var GetEmailAddress = Class.create();
//Extend global.AbstractAjaxProcessor
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//define getEmail function
	getEmail: function(){
		//create a glideRecord for the Usertable
		var userRecord = new GlideRecord("sys_user");
		//retrieve User record from the sys_user table with the userID and store it in the userRecord
		userRecord.get(this.getParameter('sysparm_userID'));
		//return email Address of the record ( + '' to make sure, that it is a string when returning);
		return userRecord.email + '';
	},
    type: 'GetEmailAddress'
});
