sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	//check if the passed in Date is in the past
	isDatePast: function(strDate){
		//create GlideDateTime objects for the current time (rightnow) and the date that should be tested
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//return true if the testDate is before rightnow, if not return false
		if(testDate.before(rightNow)){
			return true;
		}else{
			return false;
		}
	},

	isDateToday: function(strDate){
		//create GlideDateTime objects for the current time (rightnow) and the date that should be tested
		var rightNow = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//convert the variables from glideDateTime to glideDate
		rightNow = rightNow.getDate();
		testDate = testDate.getDate();

		//check if Dates are on the same Day !testDate.before(rightNow) && !rightNow.before(testDate)
		if(testDate == rightNow){
			return true;
		}else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
