sys_script_include.script:
function validateEmailAddress(emailStr){
	emailStr = emailStr + ''; //Add empt string to make sure that the input is already a string or now is converted to one
	//compare the string input with a regex. If the string matches the format required to be an email address, the function returns true, if not false
	if(emailStr.match(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}else{
		return false;
	}
}
