sysauto_script.script:
//get todays time and date
var rightNow = new GlideDateTime();

var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');
//query all tasks with a due date before now
overdueNITasks.addQuery('due_date','<=', rightNow);
//also query all records, where the state isn't 3 aka'closed'
overdueNITasks.addQuery('state','!=',3);

//run the query
overdueNITasks.query();

//query tasks that will be due within the next 24 hours
var tomorrow = new GlideDateTime();
tomorrow.addDaysLocalTime(1);
var soonOverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
soonOverdueNITasks.addQuery('due_date','>',rightNow);
soonOverdueNITasks.addQuery('due_date','<=', tomorrow);
soonOverdueNITasks.query();


while(overdueNITasks.next()){
//write the overdue tasks to the log
	gs.info("Overdue NeedIt Task record = " + overdueNITasks.number);
	//add event for each found record
	gs.eventQueue('x_58872_needit.overdueNeedItTask',overdueNITasks,overdueNITasks.number,gs.getUserName());
}

while(soonOverdueNITasks.next()){
	gs.info("Soon overdue NeedIt Tasks = " + soonOverdueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',soonOverdueNITasks,soonOverdueNITasks.number,gs.getUserName());
}
